SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET AUTOCOMMIT = 0;
START TRANSACTION;
SET time_zone = "+00:00";

DROP TABLE IF EXISTS `analytics`;
CREATE TABLE `analytics` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `page` text NOT NULL,
  `created` timestamp NOT NULL DEFAULT current_timestamp(),
  `additional` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

DROP TABLE IF EXISTS `archivalien`;
CREATE TABLE `archivalien` (
  `ID` int(11) NOT NULL,
  `Titel` varchar(100) NOT NULL,
  `Komponist` varchar(50) DEFAULT NULL,
  `Bearbeiter` varchar(50) DEFAULT NULL,
  `Dichter` varchar(50) DEFAULT NULL,
  `Setzer` varchar(50) DEFAULT NULL,
  `Typus` enum('Handschrift','Kopie von Handschrift','Druck') NOT NULL,
  `Verlag` varchar(50) DEFAULT NULL,
  `Verfassungsdatum` varchar(20) DEFAULT NULL,
  `Sprache` varchar(50) NOT NULL DEFAULT 'ger',
  `Schrift` varchar(50) NOT NULL DEFAULT 'Latn',
  `Anzahl` varchar(255) NOT NULL,
  `Sammlung` enum('Introitensammlung','Archiv') NOT NULL DEFAULT 'Archiv',
  `Standort` varchar(50) NOT NULL,
  `Bemerkungen` text DEFAULT NULL,
  `Audiolink` varchar(255) DEFAULT NULL,
  `Dokumentlink` text DEFAULT NULL,
  `Signatur` varchar(7) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `komponisten`;
CREATE TABLE `komponisten` (
  `Abk` varchar(3) NOT NULL,
  `Name` varchar(100) DEFAULT NULL,
  `Info` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;



ALTER TABLE `analytics`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `archivalien`
  ADD PRIMARY KEY (`ID`),
  ADD UNIQUE KEY `Signatur` (`Signatur`);

ALTER TABLE `komponisten`
  ADD PRIMARY KEY (`Abk`),
  ADD UNIQUE KEY `Name` (`Name`);


ALTER TABLE `analytics`
  MODIFY `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=36272;
ALTER TABLE `archivalien`
  MODIFY `ID` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=267;COMMIT;
